/*Jerome Hayes Jrhw.cpp1/27/16This program place integers enter by user into an array with 100 spots.Once that is done, the program calculate and display the variance and average.100% done*/#include <iostream>using namespace std;const int Max = 100;        //because size of array will always be 100 float average(float ray[], int size ){    float sum = 0;    for(int i=0; i<size; i++) {        sum += ray[i];    }    return sum/size;    } //end of averagefloat variance(float ray[], int size){    float a = 0;    float b = 0;    float c = 0;    for(int i=0; i<size; i++) {        a = ray[i] - average(ray, size); // a= for each number - average        b = a * a;                      // b = a square        c += b;                          // c = all of the square values added together    }    return c/size;                      //average of all the square differences} //end of varianceint main(){    float a[Max];    float input;    cin >> input;    int countsize = 0;    while(!cin.eof()) {        if(countsize > Max){        //this if statement make sure user did not enter too many numbers            cout << "You Enter Too Many Numbers!! Limit is 100!" << endl;            break;        }        *(a+countsize) = input;         //placing number into array        countsize++;        cin >> input;    }        if(countsize <= 1) {            //checks if user input more than 1 value        cout << "You did not enter enough values!!" << endl;         }    if(cin.eof() and countsize >1) {    cout << "There was a total of " << countsize << " numbers in array" << endl;        cout << "The average is " << average(a, countsize) << endl;    cout << "The variance is " << variance(a, countsize) << endl;    }}   //end of hw.cpp