#include <iostream>#include "String.h"#include  <cassert>using namespace std;int main(){   String s1; // s1 == ""   assert(s1.length() == 0);   String s2("hi");  // s2 == "hi"   assert(s2.length() == 2);   String s3(s2);  // s3 == "hi"   assert(s3.length() == 2);   assert(s3[0] == 'h');   assert(s3[1] == 'i');   s1 = s2;  // s1 == "hi"   assert(s1 == s2);   s3 = "bye";  // s3 == "bye"   assert(s3.length() == 3);   assert(s3[0] == 'b');   assert(s3[1] == 'y');   assert(s3[2] == 'e');    s3 * 3;    assert(s3.length() == 9);          s1 += "re";  // s1 == "hire"   assert(s1 == "hire");   s1 += "d"; // s1 == "hired"   assert(not (s1 == "hire"));    String s5;    s5 = "t" + "e";    assert(s5.length() == 2);    assert(s5[0] == 't');   cout << "SUCCESS" << endl;}