/*Jerome Hayes Jrbubblesort.cpp1/20/16This program sorts random generated numbers using bubble sort.100% done*/#include <iostream>#include <cstdlib>#include <vector>using namespace std;int main(){    cout << "input you desire vector size: \n";    int n;    cin >> n;    vector <int> v(n);              //vector with a set size    srand(time(0));                 //will create different random numbers everytime the program run    for(int i=0; i<n; i++)          //loop that generates random numbers    {        v[i] = rand() % 1000000;    }    for(int i = n-1; i > 0; i--) {  //bubble sort        for(int j=0; j<i; j++) {            if (v[j] > v[j+1]) {    //checking if the number before is bigger                int temp = v[j];    //swaping the two values                v[j] = v[j+1];                v[j+1] = temp;            }        }    }    for(int i =0; i < n; i++)        cout << v[i] << endl;}