/*Jerome Hayes Jrinsertionsort.cpp1/20/16This program sort random generated numbers using insertion sort. In this program you are able to input the size of the random number generated vector.100% done*/#include <iostream>#include <cstdlib>#include <vector>using namespace std;int main(){    cout << "input your desire size of the vector: \n";    int n;    cin >> n;    vector <int> v(n);              //vector with a set size    srand(time(0));                 //will randomize different numbers everytime the program runs    for(int i=0; i<n; i++)          //loop that generates random numbers    {        v[i] = rand() % 1000000;    }    int elem, j;    for(int i=1; i<n; i++) {          //insertion sort        for(elem = v[i], j=i-1; j>=0 and elem<v[j]; j--){            v[j+1] = v[j];        }        v[j+1] = elem;    }    for(int i =0; i < n; i++)        cout << v[i] << endl;}