/*Jerome Hayes Jrselectionsort.cpp1/20/16This program sort random generated numbers by using selection sort. In this program you are able to input the size of the random number generated vector.100% done*/#include <iostream>#include <cstdlib>#include <vector>using namespace std;int main(){    cout << "input your desire vector size: \n";    int n;    cin >> n;    vector <int> v(n);    srand(time(0));                     // randomize different numbers everytime the program run    for(int i=0; i<n; i++)              // loop that generate random number into vector    {        v[i] = rand() % 1000000;    }    for(int i=0; i < n-1; i++){        // i first        for(int j = i+1; j<n; j++) {   // j is set to next            if (v[i] > v[j]) {               int temp = v[i];        // swap                v[i] = v[j];                v[j] = temp;             }        }    }    for(int i =0; i < n; i++)        cout << v[i] << endl;}